// App Component
const App: React.FC = () => {
  const [bookingCode, setBookingCode] = useState('');
  const [messages, setMessages] = useState<Message[]>([]);
  const [inputText, setInputText] = useState<string>('');
  const [isListening, setIsListening] = useState(false);
  const [isConnected, setIsConnected] = useState(false);
  const [recognition, setRecognition] = useState<VoiceRecognition | null>(null);
  const [statusMessage, setStatusMessage] = useState('');

  const chatContainerRef = useRef<HTMLDivElement>(null);
  const inputRef = useRef<HTMLInputElement>(null);

  // Initialize speech recognition
  useEffect(() => {
    const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
    if (SpeechRecognition) {
      const recognition = new SpeechRecognition();
      recognition.continuous = false;
      recognition.interimResults = false;
      recognition.lang = 'en-US';

      recognition.onstart = () => {
        setIsListening(true);
        setStatusMessage('Listening...');
      };

      recognition.onend = () => {
        setIsListening(false);
        setStatusMessage('');
      };

      recognition.onerror = (event) => {
        console.error('Speech recognition error:', event.error);
        console.error('Error details:', event);
        setIsListening(false);
        setStatusMessage(`Voice recognition error: ${event.error}. Please try again.`);
      };

      recognition.onresult = (event) => {
        console.log('Speech recognition result:', event);
        console.log('Results length:', event.results.length);

        if (event.results.length > 0) {
          const result = event.results[0];
          const transcript = result.transcript;
          console.log('Voice transcript:', transcript);
          setInputText(transcript || '');
          setStatusMessage('Processing voice input...');
        } else {
          console.log('No transcript found in results');
          setStatusMessage('No speech detected. Please try again.');
        }
      };

      setRecognition(recognition);
    } else {
      setStatusMessage('Voice recognition not supported in this browser.');
    }
  }, []);

  // Auto-scroll to bottom when new messages arrive
  useEffect(() => {
    if (chatContainerRef.current) {
      chatContainerRef.current.scrollTop = chatContainerRef.current.scrollHeight;
    }
  }, [messages]);

  // Poll for new messages
  useEffect(() => {
    if (!isConnected || !bookingCode) return;

    const pollInterval = setInterval(async () => {
      try {
        const response = await axios.get(`/api/v1/get_message/${bookingCode}`);
        if (response.data.success) {
          const newMessages = response.data.data.messages || [];
          setMessages(newMessages);
        }
      } catch (error) {
        console.error('Polling error:', error);
      }
    }, 3000); // Poll every 3 seconds

    return () => clearInterval(pollInterval);
  }, [isConnected, bookingCode]);

  // Connect to booking
  const connectToBooking = async () => {
    if (!(bookingCode || '').trim()) {
      setStatusMessage('Please enter a booking code.');
      return;
    }

    try {
      setStatusMessage('Connecting to booking...');

      // Test connection by getting messages
      const response = await axios.get(`${API_ENDPOINTS.GET_MESSAGES}/${bookingCode}`);

      if (response.data.success) {
        setIsConnected(true);
        setMessages(response.data.data.messages || []);
        setStatusMessage('Connected successfully!');

        // Add welcome message
        const welcomeMessage: Message = {
          id: Date.now().toString(),
          text: `Welcome to booking ${bookingCode}! You can now chat with your driver.`,
          sender: 'ai',
          timestamp: new Date().toISOString(),
          type: 'text'
        };
        setMessages(prev => [...prev, welcomeMessage]);
      }
    } catch (error) {
      console.error('Connection error:', error);
      setStatusMessage('Failed to connect. Please check your booking code.');
    }
  };

  // Send message
  const sendMessage = async () => {
    console.log('Send message called with inputText:', inputText);
    if (!(inputText || '').trim() || !isConnected) {
      console.log('Send message blocked - inputText:', inputText, 'isConnected:', isConnected);
      return;
    }

    const newMessage: Message = {
      id: Date.now().toString(),
      text: inputText,
      sender: 'passenger',
      timestamp: new Date().toISOString(),
      type: 'text'
    };

    console.log('Adding message to UI:', newMessage);
    setMessages(prev => [...prev, newMessage]);
    setInputText('');
    setStatusMessage('Sending message...');

    try {
      const response = await axios.post(API_ENDPOINTS.SEND_MESSAGE, {
        booking_code: bookingCode,
        message: inputText,
        sender: 'passenger'
      });

      if (response.data.success) {
        setStatusMessage('Message sent successfully!');

        // Add AI response
        const aiResponse: Message = {
          id: (Date.now() + 1).toString(),
          text: 'Message sent successfully. The driver will be notified.',
          sender: 'ai',
          timestamp: new Date().toISOString(),
          type: 'text'
        };
        setMessages(prev => [...prev, aiResponse]);
      }
    } catch (error) {
      console.error('Send message error:', error);
      setStatusMessage('Failed to send message. Please try again.');
    }
  };

  // Make call
  const makeCall = async () => {
    if (!isConnected) return;

    setStatusMessage('Initiating call...');

    try {
      const response = await axios.post(API_ENDPOINTS.MAKE_CALL, {
        booking_code: bookingCode,
        call_type: 'voice',
        duration: 0
      });

      if (response.data.success) {
        const callMessage: Message = {
          id: Date.now().toString(),
          text: 'Call initiated successfully',
          sender: 'ai',
          timestamp: new Date().toISOString(),
          type: 'call',
          callDetails: {
            duration: 0,
            status: 'initiated'
          }
        };
        setMessages(prev => [...prev, callMessage]);
        setStatusMessage('Call initiated!');
      }
    } catch (error) {
      console.error('Make call error:', error);
      setStatusMessage('Failed to initiate call. Please try again.');
    }
  };

  // Start voice recognition
  const startListening = () => {
    if (recognition && !isListening) {
      recognition.start();
    }
  };

  // Handle key press
  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      sendMessage();
    }
  };

  // Poll for new messages
  useEffect(() => {
    if (!isConnected || !bookingCode) return;

    const pollInterval = setInterval(async () => {
      try {
        const response = await axios.get(`${API_ENDPOINTS.GET_MESSAGES}/${bookingCode}`);
        if (response.data.success) {
          const newMessages = response.data.data.messages || [];
          setMessages(newMessages);
        }
      } catch (error) {
        console.error('Polling error:', error);
      }
    }, 3000); // Poll every 3 seconds

    return () => clearInterval(pollInterval);
  }, [isConnected, bookingCode]);

  return (
    <AppContainer>
      <Header>
        <Title>PAX - Passenger Assistant</Title>
        <Subtitle>AI-powered chat and voice assistant</Subtitle>
      </Header>

      <MainContent>
        {!isConnected ? (
          <BookingCodeInput>
            <Input
              type="text"
              placeholder="Enter booking code..."
              value={bookingCode}
              onChange={(e) => setBookingCode(e.target.value)}
              onKeyPress={(e) => e.key === 'Enter' && connectToBooking()}
            />
            <Button
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              onClick={connectToBooking}
            >
              Connect
            </Button>
          </BookingCodeInput>
        ) : (
          <>
            <ChatContainer ref={chatContainerRef}>
              <AnimatePresence>
                {messages.map((message) => (
                  <MessageBubble
                    key={message.id}
                    $isOwn={message.sender === 'passenger'}
                    initial={{ opacity: 0, y: 20 }}
                    animate={{ opacity: 1, y: 0 }}
                    exit={{ opacity: 0, y: -20 }}
                    transition={{ duration: 0.3 }}
                  >
                    {message.sender !== 'passenger' && (
                      <Avatar $type={message.sender}>
                        {message.sender === 'driver' ? <FaCar /> : <FaUser />}
                      </Avatar>
                    )}
                    <div>
                      <MessageContent $isOwn={message.sender === 'passenger'}>
                        {message.type === 'call' ? (
                          <CallIndicator>
                            <FaPhone />
                            {message.text}
                          </CallIndicator>
                        ) : (
                          message.text
                        )}
                      </MessageContent>
                      <MessageTime>
                        {new Date(message.timestamp).toLocaleTimeString()}
                      </MessageTime>
                    </div>
                    {message.sender === 'passenger' && (
                      <Avatar $type={message.sender}>
                        <FaUser />
                      </Avatar>
                    )}
                  </MessageBubble>
                ))}
              </AnimatePresence>
            </ChatContainer>

            <InputContainer>
              <TextInput
                ref={inputRef}
                type="text"
                placeholder="Type a message or use voice..."
                value={inputText || ''}
                onChange={(e) => setInputText(e.target.value || '')}
                onKeyPress={handleKeyPress}
                disabled={!isConnected}
              />
              <VoiceButton
                $isListening={isListening}
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
                onClick={startListening}
                disabled={!isConnected || isListening}
              >
                {isListening ? <FaMicrophoneSlash /> : <FaMicrophone />}
              </VoiceButton>
              <Button
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
                onClick={sendMessage}
                disabled={!(inputText || '').trim() || !isConnected}
              >
                <FaPaperPlane />
              </Button>
              <Button
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
                onClick={makeCall}
                disabled={!isConnected}
              >
                <FaPhone />
              </Button>
            </InputContainer>
          </>
        )}

        {statusMessage && (
          <StatusMessage>{statusMessage}</StatusMessage>
        )}
      </MainContent>
    </AppContainer>
  );
};

export default App;
