/**
 * Configuration for PAX App
 * Environment-specific settings for local, staging, and production
 */

const getEnvironment = () => {
  // Check for environment variable
  if (process.env.REACT_APP_ENVIRONMENT) {
    return process.env.REACT_APP_ENVIRONMENT;
  }

  // Check hostname for environment detection
  const hostname = window.location.hostname;
  if (hostname.includes('localhost') || hostname.includes('127.0.0.1')) {
    return 'local';
  } else if (hostname.includes('staging')) {
    return 'staging';
  } else {
    return 'production';
  }
};

const configs = {
  local: {
    apiBaseUrl: 'http://localhost:8000',
    websocketUrl: 'ws://localhost:8000/ws',
    environment: 'local',
    debug: true,
    features: {
      voiceRecognition: true,
      realTimeUpdates: true,
      analytics: false
    }
  },

  staging: {
    apiBaseUrl: 'https://mcp-staging.sameer-jha.com',
    websocketUrl: 'wss://mcp-staging.sameer-jha.com/ws',
    environment: 'staging',
    debug: true,
    features: {
      voiceRecognition: true,
      realTimeUpdates: true,
      analytics: true
    }
  },

  production: {
    apiBaseUrl: 'https://mcp.sameer-jha.com',
    websocketUrl: 'wss://mcp.sameer-jha.com/ws',
    environment: 'production',
    debug: false,
    features: {
      voiceRecognition: true,
      realTimeUpdates: true,
      analytics: true
    }
  }
};

const currentConfig = configs[getEnvironment()] || configs.local;

// API endpoints
export const API_ENDPOINTS = {
  SEND_MESSAGE: `${currentConfig.apiBaseUrl}/api/v1/send_message`,
  MAKE_CALL: `${currentConfig.apiBaseUrl}/api/v1/make_call`,
  GET_MESSAGES: `${currentConfig.apiBaseUrl}/api/v1/get_message`,
  AI_AGENT: `${currentConfig.apiBaseUrl}/api/v1/ai_agent`,
  HEALTH: `${currentConfig.apiBaseUrl}/health`
};

// Configuration exports
export const CONFIG = {
  ...currentConfig,
  isLocal: () => currentConfig.environment === 'local',
  isStaging: () => currentConfig.environment === 'staging',
  isProduction: () => currentConfig.environment === 'production',
  isDebug: () => currentConfig.debug,
  getApiUrl: (endpoint) => `${currentConfig.apiBaseUrl}${endpoint}`,
  getWebsocketUrl: () => currentConfig.websocketUrl
};

// Feature flags
export const FEATURES = currentConfig.features;

// Debug logging
if (CONFIG.isDebug()) {
  console.log('ðŸ”§ PAX App Configuration:', CONFIG);
  console.log('ðŸš€ Features:', FEATURES);
}

export default CONFIG;
